const addToCartForms=document.querySelectorAll('form[action="/cart/add"]');addToCartForms.forEach(t=>{t.addEventListener("submit",async a=>{a.preventDefault(),await fetch("/cart/add",{method:"post",body:new FormData(t)});let o=await fetch("/cart.json"),e=await o.json();document.querySelectorAll(".cart-count").forEach(t=>{t.textContent=e.item_count})})});